package com.kleinreveche.adventofcode.twentytwo

fun daySix(showProblem: Boolean, showMore: Boolean = false) {
    val startPacketIndex = readDaySixInput(daySixInput, 4)
    val startMessageIndex = readDaySixInput(daySixInput, 14)
    println("--- Day 6: Tuning Trouble ---")
    if (showProblem) {
        println()
        println("The preparations are finally complete; you and the Elves leave camp on foot and begin to make your way toward the star fruit grove.\n" +
                "As you move through the dense undergrowth, one of the Elves gives you a handheld device.\n" +
                "He says that it has many fancy features, but the most important one to set up right now is the communication system.\n" +
                "However, because he's heard you have significant experience dealing with signal-based systems, \n" +
                "he convinced the other Elves that it would be okay to give you their one malfunctioning device - surely you'll have no problem fixing it.\n\n" +
                "As if inspired by comedic timing, the device emits a few colorful sparks.\n" +
                "To be able to communicate with the Elves, the device needs to lock on to their signal.\n" +
                "The signal is a series of seemingly-random characters that the device receives one at a time.\n\n" +
                "   > How many characters need to be processed before the first start-of-packet marker is detected?\n" +
                "   > How many characters need to be processed before the first start-of-message marker is detected?")
        println()
        println("Answer:")
    }
    println("   The characters that were needed to be processed before the first start-of-pocket were $startPacketIndex")
    println("   The characters that were needed to be processed before the first start-of-message were $startMessageIndex")
}

private fun readDaySixInput(input: String, charNum: Int): Int {
    var index = 0

    while (index + charNum < input.length) {
        val subString = input.substring(index, index + charNum)
        if (subString.toSet().size == charNum) {
            return index + charNum
        }
        index++
    }

    return -1
}

private val daySixInput = """
    
    """.trimIndent()